  THEN IF (OperandSize = 8)
     THEN
       AX <- AL * SRC (* Signed multiplication *)
       IF AL = AX
          THEN CF <- 0; OF <- 0;
          ELSE CF <- 1; OF <- 1; FI;
     ELSE IF OperandSize = 16
       THEN
