             IF RAX = RDX:RAX
               THEN CF <- 0; OF <- 0;
               ELSE CF <- 1; OF <- 1; FI;
          FI;
     FI;
  ELSE IF (NumberOfOperands = 2)
     THEN
       temp <- DEST * SRC (* Signed multiplication; temp is double DEST size *)
       DEST <- DEST * SRC (* Signed multiplication *)
