     Temp <- (ESP);
     Push(EAX);
     Push(ECX);
     Push(EDX);
     Push(EBX);
     Push(Temp);
     Push(EBP);
     Push(ESI);
     Push(EDI);
  ELSE (* OperandSize = 16, PUSHA instruction *)
     Temp <- (SP);
     Push(AX);
     Push(CX);
     Push(DX);
     Push(BX);
     Push(Temp);
     Push(BP);
     Push(SI);
     Push(DI);
FI;

